vec3 pass( out vec4 fragColor, in vec2 fragCoord, float time )
{
    float t = time;
    float bpm = 1.0 / 60.0 * 140.0;
    float bt = t * bpm;
    float pi = 3.14152925359;
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 nuv = uv * 2.0 - 1.0;
    nuv.y /= iResolution.x / iResolution.y;
    vec2 nuv0 = nuv;

    float must = 1.0 / 32.0;
    float mus0 = texture(iChannel0, vec2(must * 0.0)).x;
    float mus1 = texture(iChannel0, vec2(must * 26.0)).x;

    mus0 = smoothstep(0.0, 1.0, pow(mus0, 0.6));
    mus1 = pow(mus1 - 0.3, 3.0) * 2.0;

    float r = sqrt(nuv0.x * nuv0.x + nuv0.y * nuv0.y);
    float theta = atan(nuv0.y / nuv0.x);

    vec4 t0 = texture(iChannel1, uv * 0.05 + bt * 0.000001 * abs(theta) * mus1 * 0.1);
    vec4 t1 = texture(iChannel1, uv * 0.5 + bt * 0.000001 * abs(theta));

    float il = max(0.0, 1.0 - length(nuv) * 1.0);
    il = smoothstep(0.1, 0.8, il);
    float f0 = sin(abs(theta) * 35.0 + bt + t1.x * 12.0 + 2.5 * mus0) * pow(il, 4.5);
    float f1 = sin(length(nuv) * 32.0 * (1.0 - mus0 * 0.2) - bt * 2.0 + t0.x * 12.5) * pow(il, 3.5);
    float f2 = cos(theta * 8.0 - bt * 2.0) * pow(il, 3.0);
    float f3 = cos(abs(theta) + bt * 2.0 + uv.y * 4.0) * pow(il, 2.5);

    vec3 c = vec3(0.0);

    c.x = f0 * t0.x;
    c.y = f1 * (1.0 + mus0 * 0.2);
    c.z = f2 * t0.x;

    c.xy -= vec2(f3) * (0.3 + mus0 * 0.6) * t0.x;

    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float t = iTime;
    float bpm = 1.0 / 60.0 * 140.0;
    float bt = t * bpm;

    float must = 1.0 / 32.0;
    float mus0 = texture(iChannel0, vec2(must * 0.0)).x;
    float mus1 = texture(iChannel0, vec2(must * 26.0)).x;

    mus0 = pow(mus0, 0.6);
    mus1 = pow(mus1 - 0.3, 3.0) * 2.0;

    vec3 c = vec3(0.2, 0.2, 0.2);

    vec2 fragCoord0 = fragCoord + vec2(sin(bt * 0.01) * 0.01, cos(bt * 0.01) * 0.21);
    vec2 fragCoord1 = fragCoord + vec2(sin(bt * 0.02 + mus0) * 0.01, cos(bt * 0.2) * 0.2);
    vec2 fragCoord2 = fragCoord + vec2(sin(bt * 0.03 + mus1) * 0.01);

    vec3 pass0 = pass(fragColor, fragCoord0, t);
    vec3 pass1 = pass(fragColor, fragCoord1, t + 0.04 + mus0 * 0.02);
    vec3 pass2 = pass(fragColor, fragCoord2, t + 0.065);

    c.x += abs(pass0.x + pass0.y + pass0.z) * (1.0 + mus0 * 0.3);
    c.y += abs(pass1.x + pass1.y + pass1.z);
    c.z += abs(pass2.x + pass2.y + pass2.z);

  	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 nuv = uv * 2.0 - 1.0;
    nuv.y /= iResolution.x / iResolution.y;

    fragColor.xyz = c;
}
