void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 blurTex = texture(iChannel0, uv);
    vec4 originTex = texture(iChannel1, uv);

    // 乗算
    vec4 finalColor = originTex * originTex;
    // スクリーン
    finalColor = (1.0f - ( ( 1.0f - finalColor ) * ( 1.0f - blurTex ) ) );
    // 比較明
    finalColor = max( finalColor, originTex );

    float value = (sin( iTime * 3.0f ) + 1.0f ) * 0.5f;
    finalColor = mix( originTex, finalColor, value );

	fragColor = finalColor;
}

const vec4[] curve = vec4[]
(
  vec4(0.0205, 0.0205, 0.0205, 0), 
  vec4(0.0855, 0.0855, 0.0855, 0), 
  vec4(0.232,  0.232,  0.232,  0),
  vec4(0.324,  0.324,  0.324,  1), 
  vec4(0.232,  0.232,  0.232,  0), 
  vec4(0.0855, 0.0855, 0.0855, 0), 
  vec4(0.0205, 0.0205, 0.0205, 0)
);

// blur method
vec4 Blur( in vec2 uv, in vec2 offset )
{
  vec2 coords = uv - offset * 3.0;  
  vec4 color = vec4(0.0f);
  for( int l = 0; l < 7; l++ )  
  {   
    vec4 tap = texture(iChannel0, coords);
    color 	+= tap * curve[l];
    coords 	+= offset;
  }

    return color;
}

// main
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float blurSize = 3.0f;
    float textureSize = 1.0f / iResolution.x;

    vec2 uv = fragCoord.xy / iResolution.xy;    
    vec2 offset = vec2( textureSize * blurSize, 0.0f );

    fragColor = Blur( uv, offset );
}

