#define PI 3.14159265358979

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	fragColor = vec4(0.);
    for (float i = 0.; i < 200.; i++) {
        vec2 uv = fragCoord.xy / iResolution.xy;
    	uv -= 0.5; //原点中心に
        uv.x *= iResolution.x / iResolution.y; //xyの幅統一

        float s = clamp(1. - length(uv) * 3. , 0.0, 1.0) * 3.;
        uv = vec2(uv.x *2.* cos(s) - uv.y * 1.5*sin(iTime), uv.y * 1.2*cos(s) + uv.x * 2.*sin(iTime));

        uv.xy *= 0.5 + 0.4*sin(iTime);
    	float t = iTime + float(i) * 0.1;
    	uv.x += .8 * sin(t * 4.)*(.51+.49*cos(1.5*iTime)); 
    	uv.y += 0.6 * sin(t * 5.)*(.51+.49*cos(1.5*iTime)); //Change the curve

    	float c = 1.0 / ( length(uv) * length(uv) ); //中心からの距離の2乗の逆数
    	c *= 0.0001; //Adjustment of light

		fragColor += vec4(c*( .2+.1*sin(PI*iTime-i)), c *( .2+.1*cos(iTime+i*.5)), c*( .2-.1*cos(iTime+i)) , 1.0); //色変更

    }
}
