void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

float w = 0.005;
vec4 col0 = vec4(0.7,0,0,1);
vec4 col1 = vec4(1,1,0,1);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;	
    vec2 c = vec2(0.5);

    float r = 0.5 * cos(0.1*iTime);
    vec2 p = r * vec2(1.0)+c;
    vec2 d = uv - p;

    vec4 col = mix(col0, col1, 0.5+0.5*sin(iTime));

    float size = 1.0 + 0.5 * sin(iTime);
    size *= 1000.0;
    float brush = exp(-dot(d,d)*size);

   	float cs = cos(w*iTime);
    float ss = sin(w*iTime);
    mat2 rot = mat2(cs, ss, -ss, cs);

    vec2 uvt = vec2(0.5)+rot*(uv-vec2(0.5));
    fragColor = mix(texture(iChannel0, uvt), col, brush);
}
