const int step = 10;
const float radius = 0.01;
vec4 midblur(in vec2 coord) {
    vec4 ret = vec4(0,0,0,0);
    vec2 lb = coord - vec2(radius, radius);
    float inc = 2.0 * radius / float(step);
    int count = 0;
    //const int step = istep * 2;
    for (int row = 0; row < step; ++row) {
        for (int col = 0; col < step; ++col) {
            vec2 pos = lb + vec2(float(col) * inc, float(row) * inc);
            if (pos.x >= -1.0 && pos.x <= 1.0 && pos.y >= -1.0 && pos.y <= 1.0) {
        		count += 1;
            	ret += texture(iChannel0, pos);
            }
        }
    }
    return ret / float(count);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 q = fragCoord.xy / iResolution.xy;
	vec4 texel = midblur(q);
    fragColor = texel;
}
