void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = (fragCoord - 0.5 * iResolution.xy)/iResolution.xy;

    float l = length(xy);

    float b = (
        min(abs(xy.x-0.5), abs(xy.x+0.5)), 
        min(abs(xy.y-0.5), abs(xy.y+0.5))
        );

    float c = l*l + b*b;
    vec2 xy2 = xy * 3.0*c ;
    vec2 uv2 = xy2 + 0.5;


    fragColor = vec4(
        texture(iChannel0, uv2 )
    );
}
