#define PI 3.14159265
#define a30 0.16666666

void mainImage( out vec4 o, in vec2 p )
{
    vec2 R = iResolution.xy;
	vec2 uv = vec2((2.*p.x - R.x),R.y-p.y)/R;
    float r = length(uv);
    float a = acos(dot(vec2(1,0),uv)/r)/ PI;


    for(int i=0;i<6;i++)
    {
        float fi=float(i);
        if(a>a30*fi)o = vec4(a30*fi);
    }
    o += r>1.? 1. :0.;

}
