void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec4 fColor;

    vec4 L = vec4(-1.0+fragCoord.x*sin(iTime), -1.0+fragCoord.y*cos(iTime), 1.0, 1.0);
    vec4 E = vec4(0.0, 0.0, 1.0, 1.0);
    vec4 N = vec4(fragCoord.x, fragCoord.y, 0.8, 1.0);
    vec4 B = normalize( L + E );    

    vec4 ambient = vec4(1.0, 0.0, 0.1, 1.0);
    vec4 diffuseProduct = vec4(0.0, 1.0, 0.0, 1.0);
    vec4 specularProduct = vec4(0.3,0.8,0.3,1.0);
    float specular =  pow(max(dot(B,N), 0.0), 0.17);
    vec4 specularity = specular*specularProduct;

    float Kd = max( dot(L, N), 0.0 );
    vec4  diffuse = Kd*diffuseProduct;

    fColor = ambient + diffuse + specularity;
    fColor.a = 1.0;

    fragColor = fColor;
}
