void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = 2.0 * (uv) - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec3 fColor = vec3(0.0);

    fColor.r = smoothstep(0.0,0.018,(sin(iTime)*uv.y)+(cos(iTime)*uv.x));
    fColor.g = smoothstep(0.0,0.018,(sin(iTime+0.01)*uv.y)+(cos(iTime+0.01)*uv.x));
    fColor.b = smoothstep(0.0,0.020,(sin(iTime+0.02)*uv.y)+(cos(iTime+0.02)*uv.x));

	fragColor = vec4(fColor,1.0);
}
