void mainImage( out vec4 f, vec2 r )
{
	vec2 _ = r.xy / iResolution.xy;
    vec3 o =vec3(0, 1, iDate.w);
    vec3 v = o;
    for (int i = 0; i < 99;i++) {
        float l = floor(cos(floor(v.x*10.)/10.)+sin(floor(v.z*10.)/10.))/5.;
        float d = v.y-l;
        v+=vec3(_-.5,1)*d/1.5;
        if (d<.01){
            f.y=(1.-(length(v-o)/12.))*(l+1.);return;}
    }
}
