float distance(float x1, float y1, float x2, float y2)
{
    float x = x2 - x1;
    float y = y2 - y1;    
    return sqrt(x * x + y * y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float timeScale = tan(log(iTime) / 100.0) * 0.15;
    float drawScale = tan(cos(iTime * timeScale + log(iTime))) * timeScale;
    float amplitude = (iTime * timeScale) * drawScale;
    float dist = distance(fragCoord.x, fragCoord.y, iResolution.x / 2.0, iResolution.y / 2.0);
    float value;
    if(distance(fragCoord.x, fragCoord.y, iResolution.x / 2.0, iResolution.y / 2.0) < 300.0 + (tan(iTime)) * dist)
    {
        value = asin(fragCoord.x * fragCoord.y * drawScale);    
        fragColor = vec4(fragColor.b + fragColor.r + iTime, fragColor.g, fragColor.b * tan(value * amplitude), 1.0);
        if(fragColor.r < value)
        {
            fragColor = vec4(distance(fragCoord.x, fragCoord.y, 0.0, 0.0) / iResolution.x, fragColor.r, fragColor.g, 1);
        }    
        else
        {
            value = tan(fragCoord.x + iTime + fragColor.r * drawScale);
            fragColor = vec4(value * amplitude, sin(iTime * 10.0), value * amplitude, 0.5) * tan(iTime);             
        } 
    }    
    else
    {
        value = sin(tan(fragCoord.y + fragCoord.x * drawScale));
        fragColor = vec4(fragColor.g * cos(value), sin(amplitude), sin(value), 1.0) * log(value) + 0.5;        
    }
    fragColor *= (dist * tan(fragColor.r) + sin(fragColor.g)) * sin(iTime + amplitude);  
    fragColor = vec4(fragColor.g, fragColor.r, fragColor.b, 1.0);
    if(fragCoord.x > iResolution.x / 2.0)
    {
        fragColor *= tan(iTime) * -1.0;
    }
}
