float N(vec2 uv)
{
    return fract(sin(dot(uv,vec2(12.8943,4.3423)))*43242.32432);
}

float M(vec2 uv)
{
    vec2 a = floor(uv);
    vec2 b = fract(uv);
    //float d = normalize(N(a)-.5);
    float d = step(.5,N(a))*2.-1.;
    return (.5-abs(fract(b.y+d*b.x)-.5))/sqrt(2.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.yy;

    // Output to screen
    fragColor = vec4(M(uv*32.-vec2(0,5.*iTime)));
}
