void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	vec3 color = vec3(0.0, 0.0, 0.0);
	float piikit  = texture(iChannel0, vec2(uv.x/10., 1.0)).r;

	float flash = texture(iChannel0, vec2(0.12, 0.0)).r;
	float glow = (0.01 + flash*0.0012)/abs(piikit - uv.y);
	color = vec3(0.0, glow*0.5, glow);
	color += vec3(sqrt(glow*0.12));
    fragColor = vec4(color,0.2);
}

#define time iTime

vec2 rot(vec2 uv,float a){
	return vec2(uv.x*cos(a)-uv.y*sin(a),uv.y*cos(a)+uv.x*sin(a));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 uvDist = rot((fragCoord / iResolution.xy-(0.005*sin(time/7.)))/1.005+sin(time/6.)/40.,0.015*sin(time/5.));
    if(iFrame == 0) {
        fragColor = vec4(0.0,0.0,0.0,1.0);
    } else {
        vec3 prev = texture(iChannel1, uvDist).rgb;
        vec4 acol = texture(iChannel0, uv);
        float alpha = acol.a;
        vec3 bias = vec3(1.0,0.979,0.999);
        fragColor = vec4(acol.rgb + prev*(1.0-alpha)*bias, 1.0);
    }
}
