#define RES  iResolution.xy
#define ASP  RES.x / RES.y
#define TIM  iTime
#define pi   3.14159265
#define tx( p ) texture( iChannel0, p )   

void
    mainImage( out vec4 oC, in vec2 iP ) {

        vec2
            xy = iP / RES - .5,
            st = vec2( 1. - ( abs( 2. * xy.x ) )  * ( .1 + .45 + xy.y ), .85 + .15 * mod( pow( .5 + xy.y, 2. ), 1. ) );

        float
            d = ( 3. * tx( vec2( .02, .00 ) ).r + 3. * tx( vec2( .7, .0 ) ).r + 7. * tx( vec2( .15, .0 ) ).r + 5. * tx( vec2( .8, .00 ) ).r );

        vec4
            c0 = tx( 1. - st );

        float
            f = c0.r * c0.g;

        oC = vec4( d * f * c0.rg, 0., 1. ) * ( .5 + xy.y );
    }
