void mainImage( out vec4 O, vec2 I )
{
    O.xy = 1. + iMouse.xy / 2e1;
    O.y = ceil( O.y );
    I = mod( I, O.y ) - mod( I, O.y + exp( O.x - 8. ) );
    O = vec4( 1. / min( I.x, I.y ) );
    O = mix( O, fract( O * vec4( 3, 5, 9, 1 ) ), .5 + .5 * sin( iTime ) );
}
