//super lowfat slimmed verison from @coyote
void mainImage( out vec4 O, vec2 p )
{
    p += p - (O.xy= iResolution.xy);
    float t = iTime/.4,
          L = length(p/= O.y);

    O = 2.3/exp(3.*L) * texture(iChannel0,.1*vec2(atan(p.y,p.x)+t/8.,L*L*3.5-t));
    O.gb = p-p;
}
