// Much code adapted from https://www.shadertoy.com/view/Xds3zN, thanks iq to let us use it! :)

#define pi 3.141593

// Switches, you can play with them!/
#define specular
//#define shadow
#define reflections
//#define ambocc
//#define show_not_finished

struct Lamp
{
  vec3 position;
  vec3 color;
  float intensity;
  float attenuation;
};

struct RenderData
{
  vec3 col;
  vec3 pos;
  vec3 norm;
  int objnr;
};

Lamp lamps[3];

// Campera options
vec3 campos;
vec3 camtarget = vec3(0., 0., 0.);
vec3 camdir;
float fov = 4.5;

// Ambient light
const vec3 ambientColor = vec3(0.3);
const float ambientint = 0.05;

// Shading options
const float specint = 0.25;
const float specshin = 80.;
const float aoint = 0.2;
const float aoMaxdist = 15.;
const float aoFalloff = 1.1;
const float shi = 0.85;
const float shf = 0.4;

// Tracing options
const float normdelta = 0.001;
const float maxdist = 50.;
const int nbrefr = 4;
const float glass_ior = 1.5;

// Antialias. Change from 1 to 2 or more AT YOUR OWN RISK! It may CRASH your browser while compiling!
const float aawidth = 0.65;
const int aasamples = 1;

// 1D hash function
float hash( float n ){
	return fract(sin(n)*3538.5453);
}

// From https://www.shadertoy.com/view/Xds3zN
float udRoundBox( vec3 p, vec3 b, float r )
{
  return length(max(abs(p)-b,0.0))-r;
}

vec2 rotateVec(vec2 vect, float angle)
{
    vec2 rv;
    rv.x = vect.x*cos(angle) - vect.y*sin(angle);
    rv.y = vect.x*sin(angle) + vect.y*cos(angle);
    return rv;
}

// Gets the bump of the image parts
float getImageBump(vec2 uv)
{
    float b = 3. - texture(iChannel0, uv).a;
    b*= smoothstep(0.47, 0.44, abs(uv.x - 0.5));
    b*= smoothstep(0.44, 0.41, abs(uv.y - 0.5));
    return b;
}

// Gets the color of the image parts
vec3 getImageColor(vec2 uv)
{
    vec3 col = texture(iChannel0, uv).rgb;
    float b = getImageBump(uv);
    return mix(vec3(1.), col, smoothstep(0.5, 2., b));
}

// Converts the position in the world to the 2D mapping coordinates of the texture of the image parts (Buf C)
vec2 getImageMPos(vec3 pos)
{
    return pos.xy*vec2(0.09, 0.125) + vec2(0.5);
}

// Distance mapping of image
float map(vec3 pos)
{
    vec2 hmp = getImageMPos(pos);
    float hm = 0.05*getImageBump(hmp);
    return udRoundBox(pos, vec3(5., 3.4, 0.1), 0.25) - hm;
}

// Main tracing function
vec2 trace(vec3 cam, vec3 ray, float maxdist, bool inside) 
{
    float t = 0.;
    float objnr = 0.;
    vec3 pos;
    float dist;
    float dist2;

  	for (int i = 0; i < 75; ++i)
    {
    	pos = ray*t + cam;
        dist = inside?-map(pos):map(pos);
        if (dist>maxdist || abs(dist)<0.00004)
            break;
        t+= dist*0.6;
  	}
  	return vec2(t, 1);
}

// From https://www.shadertoy.com/view/MstGDM
// Here the texture maping is only used for the normal, not the raymarching, so it's a kind of bump mapping. Much faster
vec3 getNormal(vec3 pos, float e, bool inside)
{  
    vec2 q = vec2(0, e);
    return (inside?-1.:1.)*normalize(vec3(map(pos + q.yxx) - map(pos - q.yxx),
                          map(pos + q.xyx) - map(pos - q.xyx),
                          map(pos + q.xxy) - map(pos - q.xxy)));
}

// Gets the color of the sky
vec3 sky_color(vec3 ray)
{
	vec3 rc = 1.5*texture(iChannel1, ray).rgb;
    return rc;
}

// Color of the image plate
vec3 getColor(vec3 pos)
{
    vec2 hmp = getImageMPos(pos);
    return getImageColor(hmp);
}

// Fresnel reflectance factor through Schlick's approximation: https://en.wikipedia.org/wiki/Schlick's_approximation
float fresnel(vec3 ray, vec3 norm, float n2)
{
   float n1 = 1.; // air
   float angle = clamp(acos(-dot(ray, norm)), -pi/2.15, pi/2.15);
   float r0 = pow((n1-n2)/(n1+n2), 2.);
   float r = r0 + (1. - r0)*pow(1. - cos(angle), 5.);
   return clamp(0., 0.9, r);
}

// Gets the flares of the lamps (kind of non-reflective specular...)
vec3 getFlares(vec3 ray)
{
	vec3 rc = vec3(0.);
    #ifdef bulb
    rc+= 1.6*clamp(normalize(lamps[1].color)*lamps[1].intensity*specint*pow(max(0.0, dot(ray, normalize(lamps[1].position - campos))), 150.), 0., 1.);
    #endif
    return rc;
}

// From https://www.shadertoy.com/view/Xds3zN, but I changed the code
float softshadow(vec3 ro, vec3 rd, float mint, float tmax)
{
	float res = 1.0;
    float t = mint;
    for(int i=0; i<16; i++)
    {
    	float h = map(ro + rd*t);
        res = min(res, 10.0*h/t + 0.02*float(i));
        t += 0.8*clamp(h, 0.01, 0.35);
        if( h<0.001 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

float calcAO( in vec3 p, in vec3 n, float maxDist, float falloff )
{
	float ao = 0.0;
	const int nbIte = 5;
	for( int i=0; i<nbIte; i++ )
	{
		float l = hash(float(i))*maxDist;
		vec3 rd = n*l;
		ao += (l - map(p + rd.x)) / pow(1.+l, falloff);
	}
	return clamp( 1.35*(1.-ao/float(nbIte)), 0., 1.);
}


// Shading of the objects pro lamp
vec3 lampShading(Lamp lamp, vec3 norm, vec3 pos, vec3 ocol, int objnr, int lampnr)
{
	vec3 pl = normalize(lamp.position - pos);
    float dlp = distance(lamp.position, pos);
    vec3 pli = pl/pow(1. + lamp.attenuation*dlp, 2.);

    // Diffuse shading
    vec3 col = vec3(0.); //ocol*lamp.color*lamp.intensity*clamp(dot(norm, pli), 0., 1.);

    // Specular shading
    #ifdef specular
    if (dot(norm, lamp.position - pos) > 0.0)
        col+= lamp.color*lamp.intensity*specint*pow(max(0.0, dot(reflect(pl, norm), normalize(pos - campos))), specshin);
    #endif

    // Softshadow
    #ifdef shadow
    col*= shi*softshadow(pos, normalize(vec3(lamp.position.x, 4.9, lamp.position.z) - pos), shf, 100.) + 1. - shi;
    #endif

    return col;
}

// Shading of the objects over all lamps
vec3 lampsShading(vec3 norm, vec3 pos, vec3 ocol, int objnr)
{
    vec3 col = vec3(0.);
    for (int l=0; l<3; l++) // lamps.length()
        col+= lampShading(lamps[l], norm, pos, ocol, objnr, l);

    return col;
}

// Sets the position of the camera with the mouse and calculates its direction
void setCamera()
{
   vec2 iMouse2;
   if (iMouse.x==0. && iMouse.y==0.)
      iMouse2 = iResolution.xy*vec2(0.5, 0.5);
   else
      iMouse2 = iMouse.xy;

   campos = vec3(-20.*cos(0.4 + 2.8*iMouse2.x/iResolution.x),
                 20.*cos(2.*iMouse2.y/iResolution.y),
                 20.*sin(0.4 + 2.8*iMouse2.x/iResolution.x)*sin(2.*iMouse2.y/iResolution.y));
   camdir = camtarget-campos;   
}

// From https://www.shadertoy.com/view/lsSXzD, modified
vec3 GetCameraRayDir(vec2 vWindow, vec3 vCameraDir, float fov)
{
	vec3 vForward = normalize(vCameraDir);
	vec3 vRight = normalize(cross(vec3(0.0, 1.0, 0.0), vForward));
	vec3 vUp = normalize(cross(vForward, vRight));

	vec3 vDir = normalize(vWindow.x * vRight + vWindow.y * vUp + vForward * fov);

	return vDir;
}

// Tracing and rendering a ray
RenderData trace0(vec3 tpos, vec3 ray, float maxdist, bool inside)
{
    vec2 tr = trace(tpos, ray, maxdist, inside);
    float tx = tr.x;
    int objnr;
    vec3 col;
    vec3 pos = tpos + tx*ray;
    vec3 norm;

    lamps[0] = Lamp(vec3(-2., 5., 20.), vec3(1., 1., 1.), 1.8, 0.01);
    lamps[1] = Lamp(vec3(15., 3, -8.), vec3(0.9, 0.96, 1.), 1.9, 0.01);
    lamps[2] = Lamp(vec3(-16., 10., -8.), vec3(1., 0.92, 0.9), 0.4, 0.01);

    if (tx<maxdist)
    {
        norm = getNormal(pos, normdelta, inside);
        col = getColor(pos);

        // Shading
        col = ambientColor*ambientint + lampsShading(norm, pos, col, objnr);

        // Ambient occlusion
        #ifdef ambocc
        col*= 1. - aoint + aoint*vec3(calcAO(pos, norm, aoMaxdist, aoFalloff));
        //col = vec3(calcAO(pos, norm, 7., 1.1));
        #endif

        objnr = 1;
    }
    else
    {
        col = sky_color(ray);
        objnr = 0;
    }
    return RenderData(col, pos, norm, objnr);
}

// Gets the light darkening of the glass in function of the color and thickness
vec3 getGlassDens(vec3 pos, vec3 ray)
{
    vec3 posr = pos;
    vec2 hmp = getImageMPos(posr);
    vec3 glasscol = getImageColor(hmp);
    float cgh = 0.25*getImageBump(hmp);
    return pow(glasscol, 0.12 + 1.3*vec3(pow(cgh, 2.)));
    //return vec3(1.);
}


// Main render function with reflections
vec4 render(vec2 fragCoord)
{   
  vec2 uv = fragCoord.xy / iResolution.xy; 
  uv = uv*2.0 - 1.0;
  uv.x*= iResolution.x / iResolution.y;

  vec3 ray = GetCameraRayDir(uv, camdir, fov);

  RenderData traceinf = trace0(campos, ray, maxdist, false);
  vec3 col = traceinf.col + getFlares(ray);

  #ifdef reflections

  bool inside = true;
  float cior = glass_ior;
  vec3 cdens;
  vec3 glassf = vec3(1.);
  vec3 norm = traceinf.norm;
  vec3 ray_r = refract(ray, traceinf.norm, 1./glass_ior);
  vec3 ray_r2;

  if (traceinf.objnr==0)
      return vec4(col, 1.0);

    int n2;
    for (int n=0; n<nbrefr; n++)
    {
        vec3 posr = traceinf.pos;
        cdens = getGlassDens(traceinf.pos, ray);
        traceinf = trace0(traceinf.pos, ray_r, 20., inside);

        if (inside)
            glassf*= cdens;
        else
            traceinf.col+= getFlares(ray_r);

        col+= traceinf.col*glassf;
        col = clamp(col, 0., 1.);

        ray_r2 = refract(ray_r, traceinf.norm, cior);
        if (length(ray_r2)!=0.)
        {
            inside = !inside;
            cior = 1./cior;

            if (inside)
            {
                col+= sky_color(ray_r2)*glassf;
                break;
            }
        }
        else
            ray_r2 = reflect(ray_r, traceinf.norm);

        ray_r = ray_r2;
        n2 = n;
    }
    if (n2==nbrefr-1)
        col+= sky_color(ray_r2)*glassf;

    // Outer reflection
    float r = fresnel(ray, norm, glass_ior);
    col = mix(col, sky_color(reflect(ray, norm)), r);


  #endif
  return vec4(col, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    setCamera();

    // Antialiasing.
    vec4 vs = vec4(0.);
    for (int j=0;j<aasamples ;j++)
    {
       float oy = float(j)*aawidth/max(float(aasamples-1), 1.);
       for (int i=0;i<aasamples ;i++)
       {
          float ox = float(i)*aawidth/max(float(aasamples-1), 1.);
          vs+= render(fragCoord + vec2(ox, oy));
       }
    }
    vec2 uv = fragCoord.xy / iResolution.xy;

    fragColor = vs/vec4(aasamples*aasamples);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x = 1.- uv.x;
    vec3 im = texture(iChannel0, uv).xyz;
    fragColor = vec4(im, dot(im, vec3(1.)));
}

const int blur_size = 7;
const float blur_width = 1.;

float gauss(float x, float e)
{
    return exp(-pow(x, 2.)/e); 
}

// Horizontal blurring
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 pos = fragCoord.xy / iResolution.xy;
   vec4 pixval = vec4(0.);
   float tot = 0.;

   const int nb = 2*blur_size+1;

   for (int x=0; x<nb; x++)
   { 
       float x2 = blur_width*float(x-blur_size);
       vec2 ipos = pos + vec2(x2/iResolution.x, 0.);
       float g = gauss(x2, float(20*blur_size)*0.5);
       pixval+= g*texture(iChannel0, ipos);
       tot+= g;
   }
   fragColor = pixval/tot;
}

const int blur_size = 7;
const float blur_width = 1.;

float gauss(float x, float e)
{
    return exp(-pow(x, 2.)/e); 
}

// Vertical blurring
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 pos = fragCoord.xy / iResolution.xy;
   vec4 pixval = vec4(0.);
   float tot = 0.;

   const int nb = 2*blur_size+1;

   for (int x=0; x<nb; x++)
   { 
       float x2 = blur_width*float(x-blur_size);
       vec2 ipos = pos + vec2(0., x2/iResolution.x);
       float g = gauss(x2, float(20*blur_size)*0.5);
       pixval+= g*texture(iChannel0, ipos);
       tot+= g;
   }
   fragColor = pixval/tot;
}
