#define r(U,i,j,g) smoothstep(.06,.0, abs(length(U-vec2(i,j))-.85) -.08)* (g)

void mainImage( out vec4 O,  vec2 U )
{
	U /= iResolution.y;
    vec2 V = floor(U*5.);
    U = 2.*fract(U*5.) -1.;

    //if (mod(V.x+V.y,2.)==0.) O.r+=.5 , U = U.yx;
    O += max( max ( r(U,-1,0,1.+U.x) , r (U,1,0,1.-U.x)), 
              max ( r(U,0,-1,  -U.y) , r (U,0,1   ,U.y) )) -O;

    //O += max( max ( r(U,-1,0,1.+.5*U.x) , r (U,1,0,1.-.5*U.x)), 
    //          max ( r(U,0,-1,  -.5*U.y) , r (U,0,1   ,.5*U.y) )) -O;

}
