void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int iter = 70;
    float scale = 5.0/iTime;
    scale = scale*scale*scale;
    vec2 center = vec2(iMouse.x / iResolution.x, iMouse.y / iResolution.y);
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 z, c;

    c.x = 1.3333 * (uv.x - 0.5) * scale - center.x;
    c.y = (uv.y - 0.5) * scale - center.y;

    z = c;
    int ia = 0;
    for(int i = 0; i<70; i++) {
        ia = i;
        float x = (z.x * z.x - z.y * z.y) + c.x;
        float y = (z.y * z.x + z.x * z.y) + c.y;

        if((x * x + y * y) > 4.0) break;
        z.x = x;
        z.y = y;
    }

    float color = (ia == iter ? 0.0 : float(ia)) / 80.0;
    fragColor = vec4(color, color, 0, 1);
}
