void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Compute viewport coords relative to center of screen.
    vec2 center = iResolution.xy * 0.5;
    float size = min(center.x, center.y);
    vec2 view = ((fragCoord.xy - center) / size);

    // Make a pretty colour.
	fragColor = vec4(view, 0.5 + 0.5 * sin(iTime), 1.0);

    // Invert the colour if outside a (soft-edged) circle.
    float r = length(view);
    float inCircle = smoothstep(0.95, 1.05, r);
    fragColor = fragColor * inCircle + (1.0 - fragColor) * (1.0 - inCircle);
}
