void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 origin = vec2(0,0);
	vec2 xy = fragCoord.xy / iResolution.xy; 	//Condensing coordinates from 0 to 1
    vec4 vidColor = texture(iChannel0, xy); 	// color from channel 0
    vec4 texColor = texture(iChannel1, xy);	// color from channel 1
    vec4 displayColor = vidColor;
    vec4 greenscreenColor = texture(iChannel0, origin);
    //if( xy.x > 0.5) {
    //    displayColor = vec4(0.0,1.0,0.0,1.0);
    //}
    if( displayColor.g > greenscreenColor.g - 0.17 && 
        displayColor.g < greenscreenColor.g + 0.17 &&
      	displayColor.r > greenscreenColor.r - 0.17 &&
      	displayColor.r < greenscreenColor.r + 0.17 &&
      	displayColor.b > greenscreenColor.b - 0.17 &&
      	displayColor.b < greenscreenColor.b + 0.17   ){
        displayColor = texColor;
    }
    fragColor = displayColor;
}
