void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy;
    //xy.x = xy.x / iResolution.x;
    //xy.y = xy.y / iResolution.y;
    vec4 color = vec4(0.0,0.0,0.0,1.0);
    vec4 solidRed = vec4(1.0,0.0,0.0,1.0);
    if (xy.x < iResolution.x / 2.0){
        color.r = xy.x/iResolution.x;;
    }
    if (xy.x > iResolution.x / 2.0){
        color.g = xy.x/iResolution.x;;
    }
    if (xy.y < iResolution.y / 2.0){
        color.b = xy.y/iResolution.y;
    }
    if (xy.y < iResolution.y / 2.0){
        color.b = xy.x/iResolution.y;
    }

    fragColor = color;
