void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 lightPosition = vec3(sin(iTime) + 0.5, 0.5, 0.0);
    vec3 testPosition  = vec3(uv.x, uv.y, 1.2);

    float lightDistance = length(testPosition - lightPosition);

    vec3 surfaceToLightVector = normalize(testPosition - lightPosition);
    vec3 normal = vec3(0.0, 0.0, 1.0);

    float cosTheta = clamp(dot(normal, surfaceToLightVector), 0.0, 1.0);

    vec3 materialColor 	= vec3(0.5 + sin(iTime * 0.3) * 0.5, 0.5 + sin(iTime * 0.2 ) * 0.5, 1.0);
    vec3 color 			= vec3(1.0, 1.0, 1.0) * cosTheta / (lightDistance * lightDistance);

	fragColor = vec4(materialColor * color, 1.0);
}
