#define float2 vec2
#define float3 vec3
#define float4 vec4

float2 dw(float2 p, float2 c, float t)
{
    p-=c;
    float l=length(p);
    float s=sin(l-t);
    s*=1./((l+1.)*(l+1.));
    float x = (p.x*s);
    float y = (p.y*s);
    return float2(x,y);
    return float2(s*0.5+0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   	float2 uv = ((fragCoord-iResolution.xy/2.0)/iResolution.xx)*100.0;
    float2 m=((iMouse.xy-iResolution.xy/2.0)/iResolution.xx)*100.0;
    float t=iTime*15.0;
    float2 col=float2(0);
    col+=dw(uv,m,t);
    col+=dw(uv,float2(-13,8),t);
    col+=dw(uv,float2(8,15),t);
    col+=dw(uv,float2(-18,-5),t);
    col=.5+col*2.;
    fragColor = vec4(col.xxy,1);
}
