#define PI 3.14159265
#define I 6.
#define BLUE vec4(.11, .15, .2, 1.)
#define WHITE vec4(1., .98, .9, 1.)

float crosses (vec2 pos, float speed, bool back, bool still){
    float angle;
    vec2 crrt;

    float temp;
    float rtn;
    vec2 i = vec2(0.);

    for(float x = 0.; x > -10.; x--){
        for(float y = 0.; y > -8.; y--){
            crrt = pos + I * vec2(x, y) + i;

            temp = (I * x - 2. * y) / -100.;

            if (mod(speed + temp + (back ? 1.: 0.), 2.) < 1.) rtn = -1.;
            else {
                angle = max(0., mod(speed + temp - (back ? 1. : 0.), 1.) - .5) * PI * (back ? -1. : 1.);
                if (!still) crrt *= mat2(cos(angle),-sin(angle),sin(angle),cos(angle));
                rtn = 1.;
            }

            if (abs(crrt.x) < 1. && abs(crrt.y) < 3. || abs(crrt.x) < 3. && abs(crrt.y) < 1.) return rtn;
            i.x += 2.;
        }
        i = vec2(0., i.y - 2.);
    }
    return 0.;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 lM = vec2(max(iResolution.x, iResolution.y), min(iResolution.x, iResolution.y));
    vec2 pos = (fragCoord -.5 * lM) / lM.x;
    float speed = iTime * .4;
    pos = pos / .02 + vec2(18.,26.);

    float crossed = crosses(pos, speed, false, false);    
    if (crossed != 0.) fragColor = BLUE;
    if (crossed != 1.){
        if (crosses(pos + vec2(2., 4.), speed, true, false) != 0.) fragColor = WHITE;
        else if (crossed == 0.) fragColor = crosses(pos, speed, false, true) == 1. ? WHITE : BLUE;
    }
}
