vec4 contrast (vec4 col, float n){
    return 0.5+(col-0.5)*n;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	vec2 mouse = iMouse.xy/iResolution.xy;
    vec4 camCol = texture(iChannel0, uv);
    camCol = contrast(camCol, 1.);
    camCol = clamp(camCol, 0., 1.);
    float total = camCol.r+camCol.g+camCol.b;
    vec2 center = vec2(0.5);
    if(camCol.r>0.6&&camCol.g+camCol.b>1.&&camCol.g+camCol.b<1.3){
        camCol.r*=1.5;
    } else if (total<0.3){
        camCol = vec4(1.);
    }

    fragColor = vec4(camCol);
}
