void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy,
         v = (U+U - R ) / R.y;
 	mat2 m = mat2(cos( .5*iTime + vec4(0,55,33,0)));
    for (float t=.8, i=0.; i < 17.; i++)
        t *= .7+.1*sin(2.243*iTime),
        v.y = abs( v *= m ).y - t;

    O = vec4(.7,.9,1,0) * exp(-1e4*dot(v,v));
}
