void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


	fragCoord = fragCoord/iResolution.xy * vec2(3.14159,2.2) - vec2(0,1.1);

    vec3 col = vec3(0.97, 0.97, 0.97);
    float thickness = 0.01f;
    int count = 50;
    for (int i = 0; i < count; i++)
    {
        float value = cos(iTime*5.0 + float(i)/10.0 + cos(float(i))/10.0)*sin(1.7*fragCoord.x + cos(iTime)*3.14)*fragCoord.x/10.0 + 3.0*float(i-(count/2))/float(count);

        float e = 2.2/iResolution.y;
        col *= smoothstep(-e, e, abs(fragCoord.y - value) - thickness);
    }
    fragColor = vec4(col,1);
}
