float random(vec2 st)
{
    return fract(sin(dot(st.xy, vec2(12.9898,78.233))) * 43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy * 100.;
    float mouse = iMouse.x / iResolution.x;

    float randomx = random(floor(uv.yy));
    float speed = mix(.0, 80.0, randomx);

    uv.x += iTime * -speed;
    vec2 ipos = floor(uv);

    float color = random(ipos) + randomx;
    color *= step(0.2, fract(uv.y));
    color = step(mix(0.7, 1.0, mouse * 4.5), color);

    fragColor = vec4(1.0 - color);
}
