void mainImage( out vec4 fragColor, in vec2 fragCoord ) { 

    float time =0.34*iTime+1.;
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y*2.4;
    float d = length(uv);
    d = pow(d,0.4);
    float pattern = sin(uv.x * 14.0*fract(time*0.5/10.)) * sin(uv.y *(14.0*fract(time*0.5/10.)));
    d = mod((0.4),pattern * 0.2);

    d = mod(mod(log(time),14.),d*fract(d)-log(length(uv)));
    if (length(uv) <0.8) {d =0.;}
    fragColor = vec4(fract(d*1.)*0.9 ,fract(d*1.)*0.9, fract(d*1.)*0.9,1);

}
