void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalize pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;
    uv = uv * 2.0 - 1.0;  // Adjust to range [-1, 1]

    // Correct for aspect ratio
    float aspect = iResolution.x / iResolution.y;
    uv.x *= aspect;

    // Time variable for animation
    float time = iTime * 0.7;

    // Add a base layer of movement to the UV coordinates
    uv += 0.1 * vec2(sin(time + uv.y * 5.0), cos(time + uv.x * 5.0));

    // Create a dynamic twisting effect
    float twist = sin(time * 0.5 + length(uv) * 5.0) * 0.5;
    float angle = atan(uv.y, uv.x) + twist;
    float radius = length(uv);
    uv = vec2(cos(angle), sin(angle)) * radius;

    // Apply a secondary layer of distortion
    uv += 0.05 * vec2(cos(time * 2.0 + uv.y * 10.0), sin(time * 2.0 + uv.x * 10.0));

    // Hyperboloid parameters with more complex dynamics
    float k = 2.5 + 0.5 * sin(time * 3.0 + radius);  // More dynamic width control
    float a = 1.0;
    float b = 1.0;

    // Calculate the z-value based on the hyperboloid equation
    float x2 = uv.x * uv.x;
    float y2 = uv.y * uv.y;
    float z = sqrt(abs((x2 - k * y2) / (a * a - k * b * b)));

    // Generate an immersive color pattern
    vec3 baseColor = vec3(0.5 + 0.5 * cos(6.28318 * (z + time * 1.1)),
                          0.5 + 0.5 * cos(6.28318 * (z + time * 1.3 + 2.0 / 3.0)),
                          0.5 + 0.5 * cos(6.28318 * (z + time * 1.7 + 4.0 / 3.0)));

    // Apply a more immersive swirling effect
    angle = atan(uv.y, uv.x) + time * 2.0 + cos(length(uv) * 15.0);
    radius = length(uv) * (1.0 + 0.4 * sin(time * 7.0 + radius));
    vec2 swirl = vec2(cos(angle), sin(angle)) * radius;

    // Combine base color with the new swirl distortion
    vec3 color = baseColor * vec3(0.7 + 0.3 * sin(15.0 * swirl.x), 
                                  0.7 + 0.3 * sin(15.0 * swirl.y), 
                                  0.7 + 0.3 * sin(15.0 * swirl.x + swirl.y));

    // Apply a more complex color inversion logic
    if (mod(floor(15.0 * z + sin(time * 5.0) * 10.0), 2.0) == 0.0) {
        color = 1.0 - color;  // Invert colors
    }

    // Intensify the pulsating glow to enhance depth perception
    float glow = exp(-25.0 * z) * (0.5 + 0.5 * sin(time * 12.0));
    color += vec3(1.0, 0.9, 0.4) * glow;

    // Final output color
    fragColor = vec4(color, 1.0);
}
