// Gabor variant: https://www.shadertoy.com/view/lcjBz3

// paper texture from https://shadertoy.com/view/lfjfRw
// extracted and adapted from "Lover" by wyatt. https://shadertoy.com/view/fsjyR3

float H (vec2 p) {                             // --- hash
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float N (vec2 p) {                             // --- fbm noise
    p *= .1;
    float o,i;
    for (; ++i < 6.; p *= 2.1 ) {
        vec4 w = vec4( floor(p), ceil(p)  );  
        vec2 f = fract(p);
        o += mix(mix( H(w.xy), H(w.xw), f.y),
                 mix( H(w.zy), H(w.zw), f.y), 
                 f.x )                         // noise(p) 
          + .2 / exp( 2.*abs(sin(.2*p.x+.1*p.y) ));
    }
    return o/3.;
}


void mainImage(out vec4 O, vec2 u) {           // --- paper(u)
    vec2 t = vec2(floor(iTime*8.)),
         R = iResolution.xy;
    u += R*vec2( H(t), H(t+.5) ); // random offset
    t = u;

    u /= vec2(2,4);  O = vec4( .8 +.3* ( N(u+vec2(1,0)) - N(u-vec2(1,0)) ) ); // draw paper with grad
 // u /= vec2(2,4);  O = vec4( .6 +.3*  N(u)/1.5 );                           // draw "paper" without grad
 // u /= 2.;         O = .6 +.3* texture(iChannel0, u/256.).rrrr;             // draw whise noise
 // u *= 1.;         O = .6 +.3* texture(iChannel1, u/256.).rrrr;             // draw canvas

    O = sqrt((O-.8)*3.+.6);
    O -= max( 0., 7. - length(t-R+10.) );  // draw offset mark
}
