const float PI = 3.14159265359,
frequency = PI,
period = PI * sqrt(3.0)/2.0,
thickness = .02;
float wave;


float line(float x, float y, float a) {
    float v = sin(x*frequency + wave* cos((y)*period)*sign(sin(x*.5*frequency)) ) +1. - thickness*(.25+cos(a)*.5);
    return smoothstep(.2,-1.0, v/fwidth(v) );
}

vec2 rot(vec2 uv, vec2 pivot, float a) {
    float c = cos(a), s = sin(a);
    return pivot + mat2(c, -s, s, c) * ( uv - pivot ) ;
}

void mainImage( out vec4 O, vec2 u )
{
    float t = iTime;
    vec2  U =   u/iResolution.y
             * ( 9.5 + sin(t*.01) );
    U = rot(U,vec2(5.25), t*.01);

	vec2 X = U,
	     Y = rot(U, vec2( .6269, .0787)  , PI/3.),
	     Z = rot(U, vec2(-.4945,-.574175),-PI/3.);


    wave = PI/2. * sin(t+U.x*.1 + cos(U.y*.4+t)) * .5 * smoothstep(0.0,600.0,length(iMouse.xy-u));



	O = vec4( line( X.x, X.y   , 0.0 ),
	          line( Y.x, Y.y+1., 0.0 ),
	          line( Z.x, Z.y,    0.0),
                    1);
}
