float rand(vec2 co){
  return fract(sin(dot(co, vec2(12.9898, 48.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  // Normalized pixel coordinates (from 0 to 1)
  vec2 uv = fragCoord/iResolution.xy;

  float time = iTime;
  //! warning: As time goes on, this develops bands

  float TimeExaggerator = 220.;
  time *= TimeExaggerator;

  fragColor.r = rand( uv + time*.571 );
  fragColor.g = rand( uv + time*-.110 );
  fragColor.b = rand( uv + time*.242 );
  fragColor.a = 1.;
}
