void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0,uv)
    //+texture(iChannel2,uv)
    ;
}

float rand(vec2 co){
    return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453);
}

float prod(vec2 co) {
    return co.x*co.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (iFrame == 0) {
        vec2 blarg = fragCoord-iResolution.xy*0.5;
        if (rand(fragCoord*5.921)>0.00 && length(blarg) > 10. && mod(atan(blarg.y,blarg.x)*2.0+0.6,6.28)<1.2){
            fragColor = vec4(vec3(
                0.,
                rand(fragCoord/57.978),
                rand(fragCoord/95.978)),1.0);
        } else if (rand(fragCoord*5.921)>0.00 && length(blarg) > 10. && mod(atan(blarg.y,blarg.x)*2.0+0.6,6.28)<1.3){
            fragColor=vec4(1,0,0,1);
        } else {
            fragColor=vec4(0,0,0,1);
        }
    } else {


        //rand(fragCoord+iTime/1000.)
    //),1.0);

        float oo = rand(fragCoord/iResolution.xy+iTime/1000.);
        oo *= 3.14159265 * 2.0;

        // now we are 0,pi/2,pi,or 3pi/4
        vec2 srcpos = fragCoord+1.0*vec2(sin(oo),cos(oo));
        vec2 dir = 2.0*normalize(fragCoord.xy-iResolution.xy/2.0); 
        srcpos +=vec2(-dir.y,dir.x);
        fragColor=texture(iChannel0,srcpos/iResolution.xy);
        if (length(iMouse.xy - fragCoord) < 8.0 &&  iMouse.z > 0.){
            fragColor = vec4(
                rand(fragCoord.yx),
                rand(fragCoord.yx*9.6)*0.6,
                0.,1.);
        }
    }
}
