void mainImage( out vec4 Q, in vec2 U ) {
    Q = A(U)/float(iFrame);

}

#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define Main void mainImage(out vec4 Q, in vec2 U)
 #define UIF (1.0 / float(0xffffffffU))
vec4 hash44(uvec4 q)
{
	q *= uvec4(1597334673U, 3812015801U, 2798796415U, 1979697957U);
	q = (q.x ^ q.y ^ q.z ^ q.w)*uvec4(1597334673U, 3812015801U, 2798796415U, 1979697957U);
	return vec4(q) * UIF;
}
#define pi 3.141592659

Main
{
    U = 2.3*(U-.5*R)/R.y;
    vec2 U0 = 2.*U;
    for (float i = 0.; i < 100.; i++) {

        U = U/dot(U,U);
        U = abs(U)-.5;

        U0 = U0/dot(U0,U0);
        U0 = abs(U0)-.25;
    }
    float a = .5+.5*sign(max(U.x,U.y)-.5);
    float b = .5-.5*sign(max(U0.x,U0.y)-.5);
    Q = vec4(a+b);
}

Main {

    vec4 n = A(U+vec2(0,1));
    vec4 e = A(U+vec2(1,0));
    vec4 s = A(U-vec2(0,0));
    vec4 w = A(U-vec2(0,0));
    Q.xy = A(U).xy;
    Q.z = e.x-w.x;
    Q.w = n.x-s.x;

}

Main {
    Q = A(U);
    vec4 h = hash44(uvec4(U,iFrame,iTime));

    vec2 d = vec2(cos(h.x*2.*pi),sin(h.x*2.*pi));

    U -= d*h.y;
    for (float i = 0.; i < 100.; i++) {

        U += 1.5*d;
        vec4 b = B(U);
        d = normalize(d-.6*(h.w)*b.zw);
        vec4 q = pow(max(dot(vec2(0,1),d),0.),3.)*1.5*b.x*max(sin(6.*h.w-3.+vec4(1,2,3,4)),0.);
        Q += q/10.;

    }
    if (iFrame < 1) Q = vec4(0);
}
