vec3 palette( float t ){
     vec3 a = vec3(.5,.5,.5);
     vec3 b = vec3(.5,.5,.5);
     vec3 c = vec3(1.,1.,1.);
     vec3 d = vec3(0.716,0.91,0.112);
     return a+b*-sin( acos(-1.)*2.*(c+t*d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy*2.-1.;

    uv.x*=iResolution.x/iResolution.y;

    vec2 uv0=uv;

    vec3 fcol=vec3(0.);

    uv0.x+=cos(uv.y*length(uv)+iTime)/(length(uv));

    uv0.y+=sin(uv.x*length(uv)+iTime)/(length(uv));

    for (float i =1.0; i<length(uv0)*3.+1.; i++){

        uv.x+=cos(uv.y*length(uv0)+iTime)/(length(uv)+i*0.25);

        uv.y+=sin(uv.x*length(uv0)+iTime)/(length(uv)+i*0.25);

        uv*=1.5;

        uv=fract(uv);

        uv-=0.5;

        float d=length(uv)*exp(-length(uv0));

        d-=0.5;

        d=sin(d*8.+iTime)/8.;

        d=abs(d);

        d=pow(0.01/d,1.2);

        vec3 col=sin(palette(length(uv0) +i*0.4+ iTime*0.4));

        fcol+=(col/i)*d;
    }
    fragColor = vec4(fcol,1.0);
}
