float sdf_of_rect(vec2 point, vec2 center, vec2 size) {
    vec2 half_size = size * 0.5;
    vec2 delta = abs(point - center) - half_size;

    float outside_dist = length(vec2(max(delta.x, 0.0), max(0.0, delta.y)));
    float inside_dist = min(max(delta.x, delta.y), 0.0);

    return outside_dist + inside_dist;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Adaptively select the shortest axis's bounds as -0.5 to 0.5.
    float min_axis = min(iResolution.x, iResolution.y);
    vec2 coord = (fragCoord - iResolution.xy / 2.0) / min_axis;

    vec2 rect_center = vec2(0.0);
    vec2 rect_size = vec2(0.5, 0.3);

    float rect_len = sdf_of_rect(coord, rect_center, rect_size);

    bool pred = rect_len <= 0.1;
    vec3 color = pred ? vec3(1.0) : vec3(0.0);

    // Output to screen
    fragColor = vec4(color ,1.0);
}
