void mainImage( out vec4 O, vec2 u )
{
    vec2 Z;
    float i, s;

    for ( ; ++i < 30. && length(Z) < 1e3 ;  )
        Z = mat2(Z,-Z.y,Z) * Z,                       // complex mul
        Z.y = abs(Z.y),                               // without this, this is just a Mandelbrot ;-)
        Z -= .1*u/iResolution.y + vec2(1.66,-.02);

    s = i - log2(log2(dot(Z,Z))) + 3.;                // coloring scheme
    O = vec4( i > 29. ? 1. : s*s/1e3);
}
