const float TURN = acos(-1.) * 2.;
float ZAG(float x) {
    float w = fwidth(x) * 0.75;
    if (w > 0.5) {return 0.5;}
    return smoothstep(
        0.5 - w, 0.5 + w,
        1. - abs(1. - 2. * fract(x))
    );
}

#define HEX(x) (vec3(uvec3(255) & (uvec3(x) >> uvec3(16, 8, 0))) / 255.)
vec3 colormap(float hue) {
    const vec3[] cols = vec3[](
    HEX(0xffffff),
    HEX(0x00cbf8),
    HEX(0xfb20d2),
    HEX(0xf96b07),
    HEX(0xffef00),
    HEX(0x07e92d),
    HEX(0x540abd),
    HEX(0x010a31)
    );

    return mix(
        cols[int(hue) & 7],
        cols[int(hue + 1.) & 7],
        fract(hue)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = fract(iTime / 16.);
    vec2 uv = (2. * fragCoord - iResolution.xy) / length(iResolution);

    float r = length(uv);

    const float HSPEED = 8.;
    float h = ceil(
        5.5 * sqrt(
            length(dot(
                uv,
                vec2(sin(t * TURN / 2.), cos(t * TURN / 2.))
            ))
        )
    + HSPEED * t) - HSPEED * t;
    float fh = 1. - h;
    fh *= fh;
    fh *= fh;
    fh = 1. - h;

    float stripes = ZAG(
        8.5 * r / (fh - 1.)
        + 12. * t
    );

    vec3 col = colormap(
        //float(iFrame >> 1) +
        3. * (h + HSPEED * t) +
        1. * stripes
    );

    // Output to screen
    fragColor = vec4(col,1.0);
}
