void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

    float t = 0.5 + 0.5*sin(iTime);

    vec2 centered_uv = 4.0*(uv - vec2(0.5));

    float diamond_pattern = abs(centered_uv.x) + abs(centered_uv.y);
    float alternating_pattern = mod(floor(diamond_pattern - t * 2.0), 2.0);


    vec3 grey = vec3(0.5);
    vec3 dark_grey = vec3(0.3);
    vec3 color = mix(grey, dark_grey, alternating_pattern);

   fragColor = vec4(color,1.0);
}
