void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = texture(iChannel0, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0., 0., 0.);
    if (
        fragCoord.x < 12. && fragCoord.y < 100.
        || fragCoord.x < 12. && fragCoord.y > iResolution.y - 100.
        || fragCoord.x > iResolution.x && fragCoord.y > iResolution.y - 100.


    ) {
        col = vec3(1.);
        fragColor = vec4(col.x, col.y, col.z, 1.);
        return;
    }

    ivec2 c1 = ivec2(fragCoord) - ivec2(24,18);
    ivec2 c2 = ivec2(fragCoord) - ivec2(10,2);
    ivec2 c3 = ivec2(fragCoord) - ivec2(9,27);
    ivec2 c4 = ivec2(fragCoord) - ivec2(24,-9);
    ivec2 c5 = ivec2(fragCoord) - ivec2(30,-1);
    ivec2 c6 = ivec2(fragCoord) - ivec2(12,-4);

    vec4 v1 = texelFetch(iChannel0, c1, 0);
    vec4 v2 = texelFetch(iChannel0, c2, 0);
    vec4 v3 = texelFetch(iChannel0, c3, 0);
    vec4 v4 = texelFetch(iChannel0, c4, 0);
    vec4 v5 = texelFetch(iChannel0, c5, 0);
    vec4 v6 = texelFetch(iChannel0, c5, 0);

    if (mod(iTime, 1.) < .5){
       col = vec3(v1.x, v2.y, v3.z);
    } else {
        col = vec3(v4.x, v5.y, v6.z);
    }

    fragColor = vec4(col.x, col.y, col.z, 1.);
}
