#define S(a, b, t) smoothstep(a, b, t)
#define sat(x) clamp(x, 0. , 1.)

vec2 within(vec2 uv, vec4 rect)
{

    return (uv - rect.xy) / (rect.zw-rect.xy);

}

float remap01(float a, float b, float t)
{

    return (t - a) / (b - a);

}

float remap(float a, float b, float c, float d, float t)
{

    return sat(remap01(a, b, t)) * (d - c) + c;

}

vec4 Eyes(vec2 uv)
{

    uv -= .5;

    vec4 col = vec4(.6, .3, 0., 1.);

    float x = uv.x;
    float m = (x * x) * -9.;

    uv.y -= m;

    float d = length(uv);

    float s = S(.3, .29, d);

    col.a = s;

    col.rgb = mix(col.rgb, vec3(.5,.2, 0.), S(.20, .19, d));

    return col;

}

vec4 Heart(vec2 uv)
{

    vec4 col = vec4(1., .0, 0., 1.);

    vec2 tempUV = uv;

    tempUV -= .5;
    uv -= .5;

    float x = uv.x;

    float m = abs(x * .6);

    uv.y -= m;

    float d = length((uv / sin(iTime)));

    float s = S(.3, .29, d);

    col.a = s;

    col.rgb *= remap01(.9, .15, -uv.y);

    d = length(vec2(tempUV.x + .13, tempUV.y - .2));

    float spec = S(.2, .01, d) * .3;

    col.rgb = mix(col.rgb, vec3(1.), spec);

    d = length(vec2(tempUV.x - .06, tempUV.y - .2));

    spec = S(.2, .01, d) * .3;

    col.rgb = mix(col.rgb, vec3(1.), spec);

    return col;

}

vec4 Head(vec2 uv)
{

    uv.x = abs(uv.x);

    vec4 col = vec4(1., .6, 0., 1.);

    float d = length(uv);

    float s = S(.38, .37, d);

    d = length(vec2(uv.x - .2, uv.y + .10));

    float cheek = S(.2, .01, d) * .25;

    col.a = s;

    col.rgb *= remap01(-.5, -.1, uv.y);

    col.rgb = mix(col.rgb, vec3(1., 0. ,0.), cheek);

    return col;

}

vec4 Mouth(vec2 uv)
{

    uv -= .5;

    float x = uv.x;

    float m = (x * x) * 2.8;

    uv.y -= m;

    vec4 col = vec4(.6, .3, 0., 1.);

    float d = length(uv);

    float i = remap(.6, .59, -.5, .51, d);

    float s = S(.61, .60, d);

    s *= remap(-1., 1., 0.1, 2., clamp(uv.y, 0., 1.));

    col.a = s;



    return col;

}

vec4 Smiley(vec2 uv)
{

    vec4 col = vec4(0.);

    vec4 heart = Heart(within(uv, vec4(.0, .1, .4, .5)));
    vec4 heart2 = Heart(within(uv, vec4(-.5, -.4, -.1, 0.)));
    vec4 heart3 = Heart(within(uv, vec4(.1, -.5, .5, -.1)));
    vec4 mouth = Mouth(within(uv, vec4(-.1, -.2, .1, -.15)));


    vec4 head = Head(uv);



    col = mix(col, head, head.a);
    col = mix(col, heart, heart.a);
    col = mix(col, heart2, heart2.a);
    col = mix(col, heart3, heart3.a);
    col = mix(col, mouth, mouth.a);


    uv.x = abs(uv.x);

    vec4 eyes = Eyes(within(uv, vec4(.0, .023, .25, .10)));

    col = mix(col, eyes, eyes.a);

    return col;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= .5;

    uv.x *= iResolution.x/iResolution.y;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec4 smiley = Smiley(uv);

    // Output to screen
    fragColor = vec4(smiley);
}
