void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float w = 1.0/iResolution.y;
    vec2 uv = fragCoord/iResolution.xy;
    uv-=0.5;
    uv.x*=iResolution.x/iResolution.y;
    vec3 col = vec3(1, 0.88, 0.88);
    uv*=8.0;

    float angle = atan(uv.x,uv.y);
    float d= length(uv);

    float curve =d - pow(sin(angle*3.0),2.0)*1.5+cos(3.0*angle)-1.5;
    float c = smoothstep(w*10.0,-w,curve);   //曲线的边缘变化比较剧烈，增大了smooth范围
    col = mix(col,vec3(0.549, 0.784, 0.478),c);


    fragColor = vec4(col,1.0);
}
