float CircleMask(vec2 uv)
{
    float centerUv = distance(uv, vec2(0.0,0.0));

    return centerUv;
}

float SinBars(vec2 uv, float frequency, float magnitude, float power)
{
    vec2 powerUv = pow(uv, vec2(power,power));
    vec2 normalizedUv = 1.0-(sin(powerUv) * 0.5 + 0.5);
    vec2 fractionUv = fract(iTime - frequency * sin(normalizedUv) * magnitude);

    float steppedUv = smoothstep(0.49,0.51,fractionUv.y);

    float halfMask = 1.0-(step(uv.y, 0.5));

    float finalSinBars = max(halfMask,steppedUv);

    return finalSinBars;
}

float LCDBars(vec2 uv, float frequency)
{
    vec2 fractionUv = fract((iTime * 0.4) + uv * frequency);
    fractionUv = step(fractionUv, vec2(0.5,0.5));

    return fractionUv.y;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //sinBars parameters
    float sinBarsFrequency = 20.0;
    float magnitude = 4.0;
    float power = 3.0;

    //LCDBars parameters
    float lcdBarsFrequency = 100.0;

    float squareRes = min(iResolution.x, iResolution.y);

    vec2 uv = fragCoord/iResolution.xy;

    vec2 centredUv = vec2 ((fragCoord.x/squareRes) - (iResolution.x * 0.5/iResolution.y), (fragCoord.y/squareRes) - 0.5);
    float sinbars = SinBars(uv, sinBarsFrequency, magnitude, power);

    float steppedCircleFill = clamp(smoothstep(0.41,0.39,CircleMask(centredUv)),0.0,1.0);
    float steppedCircleOutline = (clamp(smoothstep(0.42,0.41,(CircleMask(centredUv))),0.0,1.0)-steppedCircleFill);

    float innerGlowMask = pow(1.0-(CircleMask(centredUv)),3.0);
    float outerGlowMask = pow(1.0-(CircleMask(centredUv)),2.4) * 1.4;
    vec3 innerGlowColor = innerGlowMask * vec3(1.0,0.733,0.816);

    float lcdBars = LCDBars(uv,lcdBarsFrequency);
    lcdBars = mix(0.95, 1.05, lcdBars);

    float innerSunMask = steppedCircleFill * innerGlowMask;
    vec3 colorFillMask = (mix(vec3(1.0,0.937,0.553)*1.1,vec3(1.0,0.5,0.816),innerSunMask))*steppedCircleFill;
    vec3 colorOutlineMask = steppedCircleOutline * vec3(1.0,0.129,0.396);
    vec3 colorTotalMask = (colorFillMask + colorOutlineMask) * lcdBars;
    colorTotalMask *= sinbars;

    float glowMask = outerGlowMask - innerGlowMask;
    vec3 colorGlowMask = glowMask * vec3(1,0.0,1.0);

    colorTotalMask += colorGlowMask;

    fragColor = vec4(colorTotalMask,0);
}
