bool higherIsMax = false;
float threshold = .5;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = fragCoord/iResolution.xy;
    vec4 tex = texture(iChannel0,uv);
    if(tex.w < threshold) tex.w = 0.;
    else if(higherIsMax) tex.w = 1.;

    //fragColor = tex;
    fragColor = vec4(vec3(tex.w),1.0); // view alpha
}
