#define GAMMA 2.2
#define SCALE 3.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 adjusted_noise = pow(texelFetch(iChannel0, ivec2(mod(fragCoord/SCALE, 1024.0)), 0).rgb, vec3(GAMMA));
    vec3 thresholded = (texture(iChannel1, uv).rgb + adjusted_noise - 1.0)*2048.0; // center around zero, scale it way up, and let clamping handle the rest
    fragColor = vec4(thresholded, 1.0);
}
