void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float intensity = 10.;

    vec2 uv = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0, uv);

    if(iFrame % 4 == 0)
    fragColor.rgb += texture(iChannel1,uv).rrr / intensity;
    else if(iFrame % 4 == 1)
    fragColor.rgb += texture(iChannel1,uv).ggg / intensity;
    else if(iFrame % 4 == 2)
    fragColor.rgb += texture(iChannel1,uv).bbb / intensity;
    else
    fragColor.rgb += texture(iChannel1,uv).aaa / intensity;

}
