#define Mode 6
// switch mode

float sdHexagram( in vec2 p, in float r )
{
    const vec4 k = vec4(-0.5,0.8660254038,0.5773502692,1.7320508076);
    p = abs(p);
    p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
    p -= 2.0*min(dot(k.yx,p),0.0)*k.yx;
    p -= vec2(clamp(p.x,r*k.z,r*k.w),r);
    return length(p)*sign(p.y);
}


float opOnion( in vec2 p, in float r )
{
  return abs(sdHexagram(p, r+2.)) - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 uv=fragCoord.xy/iResolution.xy-.5;
uv.y*=iResolution.y/iResolution.x;
    float t = iTime * 1.1 + ((.25 + .05 * sin(iTime * .1))/(length(uv.xy) + .27)) * 2.2; // borrowed from https://www.shadertoy.com/view/4ffXDf
    float ta = tan(t);
    float co = cos(t);
    mat2 ma = mat2(co, ta, -ta, co);

    uv*=ma;
    uv=uv*20.;

    if (iMouse.z > 1.) {
        uv*=iMouse.x/iResolution.x * 10.;
    }


    vec3 col = vec3(cos(opOnion(uv.xy, 1.5)));

    if (Mode == 1){
        col = vec3(cos(pow(abs(uv.x), abs(uv.y)))); // interesting
    } else if (Mode == 2) {
        col = vec3(pow(length(fract(uv)-0.5),abs(uv.y)*abs(uv.x))); // another cool thing
    } else if (Mode == 3) {
        col = vec3(mod(length(fract(uv)-0.5),abs(uv.y)*abs(uv.x))); // changed pow to mod
    } else if (Mode == 4) {
        col = vec3(dot(cos(abs(uv.x)), cos(abs(uv.y)))) * 3.; // checkered (change dot to mod for fractal lookin thing)
    } else if (Mode == 5) {
        col = vec3(mod(abs(uv.x), abs(uv.y))) * 3.; // spiraly
    } else if (Mode == 6) {
        col = vec3(mix(length(fract(uv)-0.5), cos(uv.x)*sin(uv.y), tan(pow((t+opOnion(uv.xy, 1.5)), 1.1)))); // Transition Visualization
    } else {
        col = col;
    }

    fragColor = vec4(col,1.0);
}
