void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    // [0, 1] --> [-1, 1]
    uv = uv * 2.0 - 1.0;

    // Default aspect is 1:1, we want 16:9 (or whatever) so we elongate our x-axis
    float aspect = iResolution.x / iResolution.y;
    uv.x *= aspect;

    // Length of line from screen-centre [0, 0] to current uv position
    float d = length(uv);

    // Anything within the circle has a length of 1 or less
    // The step function returns 0 if the value (d) is less then the threshold (1.0)
    float circle = step(1.0, d);

    // Output to screen
    fragColor = vec4(vec3(1.0, 1.0, 1.0) * circle, 1.0);
}
